import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import { Row, Col, Container } from "react-bootstrap";
import ImageZoom from "react-medium-image-zoom";
import { productDetails } from "../../actions/product_actions";
export default function ProductScreen({ match }) {
  const productId = match.params.id;
  const dispatch = useDispatch();

  const productDetails = useSelector((state) => state.productDetails);
  const {
    error: errorDetails,
    loading: loadingDetails,
    success: successDetails,
    product,
  } = productDetails;
  useEffect(() => {
    dispatch(listCategories());
    if (successUpdate) {
      dispatch({ type: PRODUCT_UPDATE_RESET });
      dispatch({ type: PRODUCT_DETAILS_RESET });
    } else {
      if (!product?.name || product._id !== Number(productId))
        dispatch(details(productId));
      else {
        setName(product.name);
        setDescription(product.description);
        setPrice(product.price);
        setImage(product.img);
        setInStock(product.inStock);
        setCategorieName(product.categorie["name"]);
        setCategorieId(product.categorie["_id"]);
      }
    }
  }, [product, productId, successUpdate, history, dispatch]);
  return (
    <div
      style={{ marginTop: "10vh", overflowX: "hidden", marginBottom: "5vh" }}
    >
      <Container className="p-5">
        <h1 className="text-primary">Title</h1>
        <h5 className="text-dark">Categoria: </h5>
      </Container>
      <Container>
        <Row>
          <Col md={5}>
            <ImageZoom
              image={{
                src:
                  "https://i.pinimg.com/originals/11/2b/74/112b746a2182417b2a947d949798c968.jpg",
                alt: "Golden Gate Bridge",
                className: "img",
                style: { width: "100%", height: "60vh" },
              }}
              zoomImage={{
                src:
                  "https://i.pinimg.com/originals/11/2b/74/112b746a2182417b2a947d949798c968.jpg",
                alt: "Golden Gate Bridge",
              }}
            />
          </Col>
          <Col md={7} className="p-5">
            <h1>$340.00</h1>
            <span>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent
              id ipsum lacus. Cras in felis neque. Vivamus a rhoncus lacus.
              Aliquam consequat risus laoreet, faucibus ipsum rhoncus, gravida
              ante. Proin elit nisi, vestibulum quis convallis a, maximus quis
              magna. Suspendisse potenti. Curabitur in nibh nisi. Duis aliquet
              ligula elit, facilisis eleifend metus efficitur ut. In sed ligula
              vitae eros dapibus pretium eu a nisl. Nam enim lectus, finibus id
              congue ac, vestibulum eu quam.
            </span>
          </Col>
        </Row>
      </Container>
    </div>
  );
}
