import { Image, Row, Col } from "react-bootstrap";
import "./styles.css";
export default function ProductPanel({ product }) {
  return (
    <Row className="d-flex w-100 product-panel" style={{ border: "2px solid #ccc" }}>
      <Col>
        {product?.img && (
          <Image
            className="d-block my-4 shadow"
            style={{ maxWidth: "150px", maxHeight: "150px" }}
            xs={6}
            md={4}
            src={`https://res.cloudinary.com/majo-floral-desing/${product.img}`}
          />
        )}
      </Col>
      <Col className="p-4">
        <b style={{ color: "#333" }}>{product.name}</b>
        <h4>$ {product.price}</h4>
      </Col>
    </Row>
  );
}
