import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import { Container, Image, Row, Col } from "react-bootstrap";
import Product from "../../components/Product";
import Loader from "../../components/Loader";
import Message from "../../components/Message";
import Paginator from "../../components/Paginator";

import { listCategories } from "../../actions/categorie_actions";
import { listProducts } from "../../actions/product_actions";
import { LinkContainer } from "react-router-bootstrap";

export default function CatalogueScreen() {
  const dispatch = useDispatch();
  const [categorie, setCategorie] = useState("all");
  const categorieList = useSelector((state) => state.categorieList);
  const {
    error: errorCategories,
    loading: loadingCategories,
    success: successCategories,
    categories,
  } = categorieList;

  const productList = useSelector((state) => state.productList);
  const {
    loading: loadingList,
    error: errorList,
    products,
    page,
  } = productList;
  useEffect(() => {
    dispatch(listCategories());
    dispatch(listProducts("all", 1, categorie));
  }, [categorie, dispatch]);

  return (
    <div style={{ marginBottom: "5vh", overflowX: "hidden" }}>
      <div
        className="d-flex justify-content-center w-100 align-items-start bg-secondary mt-4 p-4 text-primary main flex-column"
        style={{
          minHeight: "60vh",
          backgroundImage:
            "url(https://floresyflores.com.mx/images/redi/serv.domic.jpg)",
        }}
      >
        <h1 style={{ fontSize: "5rem" }}>Catálogo</h1>
      </div>
      <Row style={{ marginTop: "5vh" }}>
        <Col md={3} className="text-dark">
          {loadingCategories ? (
            <Loader />
          ) : errorCategories ? (
            <Message variant="danger">{errorCategories}</Message>
          ) : (
            <div className="bg-light">
              {categories?.map((category) => (
                <div className="p-3">
                  <h5
                    style={{ cursor: "pointer" }}
                    onClick={() => setCategorie(category._id)}
                  >
                    {category.name}
                  </h5>
                </div>
              ))}
            </div>
          )}
        </Col>
        <Col md={9} className="bg-light">
          <div className=" p-2" style={{ minHeight: "60vh" }}>
            <Row>
              {loadingList ? (
                <Loader />
              ) : errorList ? (
                <Message variant="danger">{errorList}</Message>
              ) : products?.length == 0 ? (
                <Message variant="primary" style={{width:"100%"}}>
                  No hay productos para esta categoría
                </Message>
              ) : (
                products?.map((product) => (
                  <LinkContainer
                    to={`/producto/${product._id}/`}
                    style={{ cursor: "pointer" }}
                  >
                    <Col sm={12} md={6} lg={4} xl={3}>
                      <Product product={product} />
                    </Col>
                  </LinkContainer>
                ))
              )}
            </Row>
          </div>
        </Col>
      </Row>
    </div>
  );
}
