import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";

import { Button, Form } from "react-bootstrap";
import FormContainer from "../../components/FormContainer";

import Loader from "../../components/Loader";
import Message from "../../components/Message";
import Paginator from "../../components/Paginator";

import { productDetails, updateProduct } from "../../actions/product_actions";
import { listCategories } from "../../actions/categorie_actions";
import {
  PRODUCT_DETAILS_RESET,
  PRODUCT_UPDATE_RESET,
} from "../../constants/product_contants";
export default function EditProductScreen({ match, history }) {
  /*Vars for creation of product*/
  const productId = match.params.id;
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [image, setImage] = useState("");
  const [isUploading, setIsUploading] = useState(false);

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const dispatch = useDispatch();

  const categorieList = useSelector((state) => state.categorieList);
  const {
    error: errorCategories,
    loading: loadingCategories,
    success: successCategories,
    categories,
  } = categorieList;

  const productDetails = useSelector((state) => state.productDetails);
  const {
    error: errorDetails,
    loading: loadingDetails,
    success: successDetails,
    product,
  } = productDetails;

  const productUpdate = useSelector((state) => state.productUpdate);
  const {
    error: errorUpdate,
    loading: loadingUpdate,
    success: successUpdate,
  } = productUpdate;

  useEffect(() => {
    dispatch(listCategories());
    if (successUpdate) {
      dispatch({ type: PRODUCT_UPDATE_RESET });
      dispatch({ type: PRODUCT_DETAILS_RESET });
    }else{
      if(!product?.name || product._id !== Number(productId))
      dispatch(upda)
    }
  }, [dispatch]);

  const uploadFileHandler = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();

    formData.append("image", file);
    formData.append("product_id", productId);

    setIsUploading(true);

    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${userInfo.token}`,
        },
      };
      const { data } = await axios.post(
        "/api/products/upload/",
        formData,
        config
      );
      setImage(data);
      setIsUploading(false);
    } catch (error) {
      setIsUploading(false);
    }
  };
  return (
    <div style={{ marginTop: "10vh" }}>
      <>
        <FormContainer>
          <h1 style={{ width: "100%" }}>Editar producto</h1>
          <Form>
            <Form.Group controlId="name">
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Ingresa el nombre"
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="desc">
              <Form.Label>DescripciÃ³n</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Ingresa los detalles"
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="name">
              <Form.Label>Precio</Form.Label>
              <Form.Control
                type="number"
                placeholder="Ingresa el precio"
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId="categorie">
              <Form.Label>CategorÃ­a</Form.Label>
              <Form.Control as="select" custom>
                {successCategories &&
                  categories?.map((categorie) => (
                    <option>{categorie.name}dsds</option>
                  ))}
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="image">
              <Form.Label>Imagen</Form.Label>
              {/* {service?.image && (
                <Image
                  className="d-block my-4"
                  style={{ maxWidth: "330px" }}
                  xs={6}
                  md={4}
                  src={`https://res.cloudinary.com/dental-clark/${service.image}`}
                />
              )} */}
              <Form.Control
                type="text"
                placeholder="Ingresa la imagen"
                value={image}
                onChange={(e) => setImage(e.target.value)}
                readOnly={true}
              ></Form.Control>
              <Form.File
                id="image-file"
                label="Elegir un archivo"
                custom
                onChange={uploadFileHandler}
              ></Form.File>
              {isUploading && <Loader />}
            </Form.Group>
            <Form.Group controlId="stock">
              <Form.Check
                type={"checkbox"}
                label={"Â¿Disponible en stock?"}
              ></Form.Check>
            </Form.Group>
            <Button variant="primary">Actualizar</Button>
          </Form>
        </FormContainer>
      </>
    </div>
  );
}
