import React, { useState, useEffect } from "react";
import { LinkContainer } from "react-router-bootstrap";
import { Table, Button, Row, Col, Container } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../../components/Loader";
import Message from "../../components/Message";
import Paginator from "../../components/Paginator";

import { listProducts, registerProduct } from "../../actions/product_actions";
import { PRODUCT_REGISTER_RESET } from "../../constants/product_contants";
export default function ProductListScreen({ history }) {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const productRegister = useSelector((state) => state.productRegister);
  const {
    loading: loadingCreate,
    success: successCreate,
    product: productCreate,
    error: errorCreate,
  } = productRegister;

  const productList = useSelector((state) => state.productList);
  const {
    loading: loadingList,
    error: errorList,
    products,
    page,
  } = productList;
  useEffect(() => {
    dispatch({ type: PRODUCT_REGISTER_RESET });

    !userInfo.isAdmin ? history.push("/") : dispatch(listProducts("all", 1));
    successCreate &&
      history.push(`/admin/producto/${productCreate._id}/editar/`);
  }, [dispatch, history, userInfo, successCreate, productCreate]);

  const createProductHandler = () => {
    dispatch(registerProduct());
  };
  return (
    <Container style={{ marginTop: "15vh" }}>
      <main>
        <Row>
          <Col>
            <h1>Productos</h1>
          </Col>
          <Col className="text-right">
            <Button className="py-3" onClick={createProductHandler}>
              <i className="fas fa-plus"></i> Crear producto
            </Button>
          </Col>
        </Row>
        <Row>
          {loadingList ? (
            <Loader />
          ) : errorList ? (
            <Message variant="danger">{errorList}</Message>
          ) : (
            <Table striped bordered hover responsive className="table-sm">
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Precio</th>
                  <th>Descripción</th>
                  <th>¿Esta en stock?</th>
                  <th>Categoría</th>
                </tr>
              </thead>
              <tbody>
                {products?.map((product) => (
                  <tr key={product._id}>
                    <td>{product.name}</td>
                    <td>${product.price}</td>
                    <td>{product.description.substr(0, 10)}</td>
                    <td>
                      {product.inStock == true ? (
                        <i
                          className="fas fa-check"
                          style={{ color: "green" }}
                        ></i>
                      ) : (
                        <i
                          className="fas fa-times-circle"
                          style={{ color: "red" }}
                        ></i>
                      )}
                    </td>
                    <td>{product.category}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Row>
      </main>
    </Container>
  );
}
