import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";

import { Col, Row, Form } from "react-bootstrap";
import FormContainer from "../../components/FormContainer";

import {
  CATEGORIE_LIST_REQUEST,
  CATEGORIE_LIST_FAIL,
} from "../../constants/categorie_contants";

export default function RegisterProductScreen() {
  /*Vars for creation of product*/
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = userState(0);
  const [image, setImage] = useState("");
  const [isUploading, setIsUploading] = useState(false);

  const dispatch = useDispatch();

  const categorieList = useSelector((state) => state.categorieList);
  const {
    error: errorCategories,
    loading: loadingCategories,
    categories,
  } = categorieList;
  

  return (
    <div style={{ marginTop: "10vh" }}>
      <>
        <FormContainer>
          <FormContainer>
            <h1 style={{ width: "100%" }}>Registrar producto</h1>
            <Form.Group controlId="nkame">
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Ingresa el nombre"
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="desc">
              <Form.Label>Descripción</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Ingresa los detalles"
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="name">
              <Form.Label>Precio</Form.Label>
              <Form.Control
                type="number"
                placeholder="Ingresa el precio"
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="stock">
              <Form.Check
                type={"checkbox"}
                label={"¿Disponible en stock?"}
              ></Form.Check>
            </Form.Group>
            <Form.Group controlId="categorie">
              <Form.Label>Categoría</Form.Label>
              <Form.Control as="select" custom>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="image">
              <Form.Label>Imagen</Form.Label>
              {/* {service?.image && (
                <Image
                  className="d-block my-4"
                  style={{ maxWidth: "330px" }}
                  xs={6}
                  md={4}
                  src={`https://res.cloudinary.com/dental-clark/${service.image}`}
                />
              )} */}
              <Form.Control
                type="text"
                placeholder="Ingresa la imagen"
                //value={image}
                //onChange={(e) => setImage(e.target.value)}
                readOnly={true}
              ></Form.Control>
              <Form.File
                id="image-file"
                label="Elegir un archivo"
                custom
                //onChange={uploadFileHandler}
              ></Form.File>
              {/* {uploading && <Loader />} */}
            </Form.Group>
          </FormContainer>
        </FormContainer>
      </>
    </div>
  );
}
