import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import { Container, Image, Row, Col } from "react-bootstrap";
import Product from "../../components/Product";
import Loader from "../../components/Loader";
import Message from "../../components/Message";
import Paginator from "../../components/Paginator";

import {
  productDetails as details,
  updateProduct,
} from "../../actions/product_actions";
import { listCategories } from "../../actions/categorie_actions";

export default function CatalogueScreen() {
  const dispatch = useDispatch();

  const categorieList = useSelector((state) => state.categorieList);
  const {
    error: errorCategories,
    loading: loadingCategories,
    success: successCategories,
    categories,
  } = categorieList;

  const productList = useSelector((state) => state.productList);
  const {
    loading: loadingList,
    error: errorList,
    products,
    page,
  } = productList;
  useEffect(() => {
    dispatch(listProducts("all", 1));
  }, []);
  return (
    <div style={{ marginTop: "10vh", overflowX: "hidden" }}>
      <div className="p-5 text-primary ">
        <h2 className="text-uppercase">
          Donde florecen las flores, hay esperanza.
        </h2>
        <h5 className="text-dark">Busca el regalo perfecto</h5>
      </div>
      <Row>
        <Col md={3} className="text-dark">
          <div className="bg-light">
            <div className="p-3">
              <h5>Categoria 1</h5>
            </div>
            <div className="p-3">
              <h5>Categoria 1</h5>
            </div>
            <div className="p-3">
              <h5>Categoria 1</h5>
            </div>
          </div>
        </Col>
        <Col md={9} className="bg-light">
          <div className=" p-2" style={{ minHeight: "60vh" }}>
            <Row>
              <Col sm={12} md={6} lg={4} xl={3}>
                <Product />
              </Col>
            </Row>
          </div>
        </Col>
      </Row>
    </div>
  );
}
