import React from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Navbar,
  Nav,
  Container,
  Row,
  Col,
  Form,
  FormControl,
  NavDropdown,
  Button,
} from "react-bootstrap";
import { Link } from "react-router-dom";
import { LinkContainer } from "react-router-bootstrap";
import { useState, useEffect } from "react";

import { USER_LOGOUT } from "../../constants/user_constants";
export default function NavigationBar() {
  const dispatch = useDispatch();

  const logoutHandler = () => {
    dispatch({ type: USER_LOGOUT });
    localStorage.removeItem("userInfo");
  };

  const userLogin = useSelector((state) => state.userLogin);
  const { error, loading, userInfo } = userLogin;

  return (
    <>
      <Navbar
        fixed="top"
        expand="lg"
        collapseOnSelect
        className="navbar navbar-expand-lg navbar-light bg-light"
      >
        <Navbar.Brand>Majo Floral Design</Navbar.Brand>
        <Navbar.Toggle aria-controls="navigationBar" />
        <Navbar.Collapse id="navigationBar">
          <Nav className="ml-auto">
            {userInfo && (
              <NavDropdown title="Admin" id="navDropdown">
                <NavDropdown.Item>
                  <LinkContainer>
                  
                  Registrar producto
                  </LinkContainer>
                  
                  </NavDropdown.Item>
                <NavDropdown.Item>Productos</NavDropdown.Item>
                <NavDropdown.Item onClick={() => logoutHandler()}>
                  Cerrar sesi√≥n
                </NavDropdown.Item>
              </NavDropdown>
            )}
            <Nav.Link>
              <LinkContainer to="/">Inicio</LinkContainer>
            </Nav.Link>
            <Nav.Link>
              <LinkContainer to="/catalogo">Cat√°logo</LinkContainer>
            </Nav.Link>
          </Nav>
          <Form inline>
            <FormControl type="text" placeholder="Search" className="mr-sm-2" />
            <Button variant="outline-primary">Search</Button>
          </Form>
        </Navbar.Collapse>
      </Navbar>
    </>
  );
}
