import React from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Navbar,
  Nav,
  Form,
  FormControl,
  NavDropdown,
  Button,
} from "react-bootstrap";
import { Link } from "react-router-dom";
import { LinkContainer } from "react-router-bootstrap";
import { useState, useEffect } from "react";

import { USER_LOGOUT } from "../../constants/user_constants";

import { registerProduct } from "../../actions/product_actions";
export default function NavigationBar() {
  const dispatch = useDispatch();

  const logoutHandler = () => {
    dispatch({ type: USER_LOGOUT });
    localStorage.removeItem("userInfo");
  };

  const userLogin = useSelector((state) => state.userLogin);
  const { error, loading, userInfo } = userLogin;

  const productRegister = useSelector((state) => state.productRegister);
  const {
    loading: loadingCreate,
    success: successCreate,
    product: productCreate,
    error: errorCreate,
  } = productRegister;

  useEffect(() => {

  }, [dis]);
  return (
    <>
      <Navbar
        fixed="top"
        expand="lg"
        collapseOnSelect
        className="navbar navbar-expand-lg navbar-light bg-light"
      >
        <Navbar.Brand>Majo Floral Design</Navbar.Brand>
        <Navbar.Toggle aria-controls="navigationBar" />
        <Navbar.Collapse id="navigationBar">
          <Nav className="ml-auto">
            {userInfo && (
              <NavDropdown title="Admin" id="navDropdown">
                <LinkContainer to="admin/producto/editar">
                  <NavDropdown.Item>Registrar producto</NavDropdown.Item>
                </LinkContainer>

                <LinkContainer to="/admin/productos">
                  <NavDropdown.Item>Productos</NavDropdown.Item>
                </LinkContainer>
                <NavDropdown.Item onClick={() => logoutHandler()}>
                  Cerrar sesión
                </NavDropdown.Item>
              </NavDropdown>
            )}

            <LinkContainer to="/">
              <Nav.Link>Inicio</Nav.Link>
            </LinkContainer>

            <LinkContainer to="/catalogo">
              <Nav.Link>Catálogo</Nav.Link>
            </LinkContainer>
          </Nav>
          <Form inline>
            <FormControl type="text" placeholder="Search" className="mr-sm-2" />
            <Button variant="outline-primary">Search</Button>
          </Form>
        </Navbar.Collapse>
      </Navbar>
    </>
  );
}
